import React, { Component } from "react";
import { Button } from "reactstrap";

class CreateStudent extends Component {
  constructor() {
    super();

    this.state = {
        studentAddress: '',
        studentName: '',
        studentDOB: '',
        studentID: '',
    }

    this.onStudentFieldChange = this.onStudentFieldChange.bind(this)
  }

  onStudentFieldChange (nameOfTheField) {
      return (e) => {
        let value = e.target.value
        this.setState({
            [nameOfTheField]: value
        })
      }
  }

  render() {
    return (
      <div>
        <h2>Create student {this.props.name}</h2>
        <form onSubmit={this.handleSubmit}>
          Student Address: <br />
          <input value={this.state.studentAddress} onChange={this.onStudentFieldChange('studentAddress')} type="text" name="studentAddress" />
          <br />
          Student Name: <br />
          <input value={this.state.studentName} onChange={this.onStudentFieldChange('studentName')} type="text" name="studentName" />
          <br />
          Date of Birth: <br />
          <input value={this.state.studentDOB} onChange={this.onStudentFieldChange('studentDOB')} type="date" name="studentDOB" />
          <br />
          Student ID: <br />
          <input value={this.state.studentID} onChange={this.onStudentFieldChange('studentID')} type="text" name="studentID" />
          <br />
          <br />
          <input type="submit" id="createSubmit" value="Add student" />
        </form>
        <table>
          <thead>
            <th>Tx Receipt Category</th>
            <th />
            <th>Value</th>
          </thead>
          <tbody>
            <tr>
              <td>Tx #</td>
              <td>:</td>
              <td>
                0xb545607fcd419e5cff6eb5c2b6c9fadded105691cb7f7fbc6be69221a27e7702
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}
export default CreateStudent;
